def getDiscriminatorPlots(data = None, discrname = ""):
    discriminatorPlots = []
    discriminatorPlots += twodimplots(data)
    return discriminatorPlots

def add_data_plots(plots, data):
    plotnames = []
    for plot in plots:
        plotnames.append(plot.name)
    data.datavariables.extend(plotnames)

